package curso;

public class Aula16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double test0 = 10;

		double test1 = 10;
		double test2 = 10;
		double test3 = 10;
		double test4 = 10;
		double test5 = 10;

		test0 = 10; // test recebe o valor 10
		test1 += 10; // test1 recebe o valor de test1 + 10
		test2 -= 10; // test2 recebe o valor de test2 - 10
		test3 *= 10; // test3 recebe o valor de test3 * 10
		test4 /= 10; // test recebe o valor de test4 / 10
		test5 %= 10; // test recebe o valor de test5 %(mod) 10

		System.out.println(test0);
		System.out.println(test1);
		System.out.println(test2);
		System.out.println(test3);
		System.out.println(test4);
		System.out.println(test5);

	}

}
